mapper.select:

select 

from g_commit_history
where repos_name = #{reposName,jdbcType=VARCHAR}
  and commit_id = #{commitId,jdbcType=VARCHAR}

mapper.delete:

delete from g_commit_history
where repos_name = #{reposName,jdbcType=VARCHAR}
  and commit_id = #{commitId,jdbcType=VARCHAR}

mapper.insert:
insert into g_commit_history (repos_name, commit_id, id, author_name, commit_time, full_message, branch_name, email_address, create_time, update_time) VALUES
(#{item.reposName,jdbcType=VARCHAR}, #{item.commitId,jdbcType=VARCHAR}, #{item.id,jdbcType=BIGINT}, #{item.authorName,jdbcType=VARCHAR}, #{item.commitTime,jdbcType=TIMESTAMP}, #{item.fullMessage,jdbcType=VARCHAR}, #{item.branchName,jdbcType=VARCHAR}, #{item.emailAddress,jdbcType=VARCHAR}, #{item.createTime,jdbcType=TIMESTAMP}, #{item.updateTime,jdbcType=TIMESTAMP})
ON DUPLICATE KEY UPDATE update_time=#{updateTime,jdbcType=TIMESTAMP};

mapper.update:

update g_commit_history
set id = #{id,jdbcType=BIGINT},
  author_name = #{authorName,jdbcType=VARCHAR},
  commit_time = #{commitTime,jdbcType=TIMESTAMP},
  full_message = #{fullMessage,jdbcType=VARCHAR},
  create_time = #{createTime,jdbcType=TIMESTAMP},
  update_time = #{updateTime,jdbcType=TIMESTAMP}
where repos_name = #{reposName,jdbcType=VARCHAR}
  and commit_id = #{commitId,jdbcType=VARCHAR}
